package com.model;

public class CityModel {
	private int id;
	private String name;

	public CityModel() {
		super();
		// TODO Auto-generated constructor stub
	}

	public CityModel(String name) {
		super();
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

}
