package com.model;

public class StateModel {
	private int id;
	private String name;

	public StateModel() {
		super();
		// TODO Auto-generated constructor stub
	}

	public StateModel(String name) {
		super();
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

}
